import{_ as a,c as r,o as t,a7 as i}from"./chunks/framework.odPblmUY.js";const h=JSON.parse('{"title":"Mermaid Chart","description":"","frontmatter":{"editLink":"https://github.com/mermaid-js/mermaid/edit/develop/packages/mermaid/src/docs/ecosystem/mermaid-chart.md","head":[["link",{"rel":"canonical","href":"https://mermaid.ai/open-source/ecosystem/mermaid-chart.html"}]]},"headers":[],"relativePath":"ecosystem/mermaid-chart.md","filePath":"ecosystem/mermaid-chart.md"}'),o={name:"ecosystem/mermaid-chart.md"};function n(s,e,m,l,d,c){return t(),r("div",null,[...e[0]||(e[0]=[i('<h1 id="mermaid-chart" tabindex="-1">Mermaid Chart <a class="header-anchor" href="#mermaid-chart" aria-label="Permalink to &quot;Mermaid Chart&quot;">​</a></h1><p>The Future of Diagramming &amp; Visual Collaboration</p><p>Try the Ultimate AI, Mermaid, and Visual Diagramming Suite by creating an account at <a href="https://www.mermaidchart.com/app/sign-up" target="_blank" rel="noreferrer">Mermaid Chart</a>.</p><br><p><a href="https://www.producthunt.com/products/mermaid-chart?utm_source=badge-follow&amp;utm_medium=badge&amp;utm_souce=badge-mermaid-chart" target="_blank"><img src="https://api.producthunt.com/widgets/embed-image/v1/follow.svg?product_id=552855&amp;theme=light" alt="Mermaid Chart - A smarter way to create diagrams | Product Hunt" style="width:250px;height:54px;" width="250" height="54"></a></p><h2 id="about" tabindex="-1">About <a class="header-anchor" href="#about" aria-label="Permalink to &quot;About&quot;">​</a></h2><p><a href="https://www.mermaidchart.com" target="_blank" rel="noreferrer">Mermaid Chart</a> was born out of the Mermaid open source project and was founded by Knut Sveidqvist together with Open Core Ventures. The lead developers from Mermaid have joined the company and there is a strong connection between the project we all love and Mermaid Chart. Mermaid Chart brings resources to the open source development of Mermaid and makes it possible to work with Mermaid professionally.</p><h2 id="features" tabindex="-1">Features <a class="header-anchor" href="#features" aria-label="Permalink to &quot;Features&quot;">​</a></h2><ul><li><p><strong>Editor</strong> - A web based editor for creating and editing Mermaid diagrams.</p></li><li><p><strong>Mermaid AI</strong> - Use our embedded AI Chat to generate diagrams from natural language descriptions.</p></li><li><p><strong>Whiteboard</strong> - A virtual whiteboard for creating and editing Mermaid diagrams.</p></li><li><p><strong>Plugins</strong> - A plugin system for extending the functionality of Mermaid.</p><p>Official Mermaid Chart plugins:</p><ul><li><a href="https://chatgpt.com/g/g-684cc36f30208191b21383b88650a45d-mermaid-chart-diagrams-and-charts" target="_blank" rel="noreferrer">Mermaid Chart GPT</a></li><li><a href="https://marketplace.atlassian.com/apps/1234056/mermaid-chart-for-confluence?hosting=cloud&amp;tab=overview" target="_blank" rel="noreferrer">Confluence</a></li><li><a href="https://marketplace.atlassian.com/apps/1234810/mermaid-chart-for-jira?tab=overview&amp;hosting=cloud" target="_blank" rel="noreferrer">Jira</a></li><li><a href="https://marketplace.visualstudio.com/items?itemName=MermaidChart.vscode-mermaid-chart" target="_blank" rel="noreferrer">Visual Studio Code</a></li><li><a href="https://plugins.jetbrains.com/plugin/23043-mermaid-chart" target="_blank" rel="noreferrer">JetBrains IDE</a></li><li><a href="https://gsuite.google.com/marketplace/app/mermaidchart/947683068472" target="_blank" rel="noreferrer">Google Docs</a></li><li><a href="https://appsource.microsoft.com/en-us/product/office/WA200006214?tab=Overview" target="_blank" rel="noreferrer">Microsoft PowerPoint and Word</a></li></ul><p>Visit our <a href="https://www.mermaidchart.com/plugins" target="_blank" rel="noreferrer">Plugins</a> page for more information.</p></li><li><p><strong>Collaboration</strong> - A web based collaboration feature for multi-user editing on Mermaid diagrams in real-time (Pro and Enterprise plans).</p></li><li><p><strong>Comments</strong> - Enhance collaboration by adding comments to diagrams.</p></li><li><p><strong>Presentations</strong> - A presentation mode for viewing Mermaid diagrams in a slideshow format.</p></li></ul><h2 id="plans" tabindex="-1">Plans <a class="header-anchor" href="#plans" aria-label="Permalink to &quot;Plans&quot;">​</a></h2><ul><li><p><strong>Free</strong> - A free plan that includes six diagrams.</p></li><li><p><strong>Pro</strong> - A paid plan that includes unlimited diagrams, access to the collaboration feature, and more.</p></li><li><p><strong>Enterprise</strong> - A paid plan for enterprise use that includes all Pro features, and more.</p></li></ul><p>To learn more, visit our <a href="https://mermaidchart.com/pricing" target="_blank" rel="noreferrer">Pricing</a> page.</p><p>Mermaid Chart is currently offering a 7-day free trial on our Pro and Enterprise tiers. Sign up for a free account at <a href="https://www.mermaidchart.com/app/sign-up" target="_blank" rel="noreferrer">Mermaid Chart</a>.</p><h2 id="mermaid-js-contributions" tabindex="-1">Mermaid JS contributions <a class="header-anchor" href="#mermaid-js-contributions" aria-label="Permalink to &quot;Mermaid JS contributions&quot;">​</a></h2><p>First time contributors are eligible for a free Pro tier account for 1 year.</p>',15)])])}const u=a(o,[["render",n]]);export{h as __pageData,u as default};
